/** Test [Msg].[LastMessageView] **/
declare @u1 uniqueidentifier; declare @u2 uniqueidentifier; declare @msg table ([Num] int IDENTITY(1,1), [Id] uniqueidentifier, [Idx] bigint, [Idx2] bigint);
set @u1 = NEWID(); set @u2 = NEWID();
--prepare data insert into [Usr].[User] ([Id], [FullName]) values (@u1, 'user1'); insert into [Usr].[User] ([Id], [FullName]) values (@u2, 'user2');
insert into [Usr].[Transport] ([UserId], [TransportTypeId]) values (@u1, /**FLChat**/0); insert into [Usr].[Transport] ([UserId], [TransportTypeId], [TransportOuterId]) values (@u1, /**Test**/-1, cast(@u1 as nvarchar(255)));
insert into [Usr].[Transport] ([UserId], [TransportTypeId]) values (@u2, /**FLChat**/0); insert into [Usr].[Transport] ([UserId], [TransportTypeId], [TransportOuterId]) values (@u2, /**Test**/-1, cast(@u2 as nvarchar(255)));
--#1 must be empty, because has not messages yet select * from [Msg].[LastMessageView] where [UserId] in (@u1, @u2);
--send first message flchat->flchat insert into @msg exec [Test].[SendMessage] @from=@u1, @fromTrans=0, @to=@u2, @toTrans=0;
--#2: first message for user1 select case when l.[MsgId] = (select [Id] from @msg where [Num] = 1) and l.[ToTransportTypeId] = 0 and l.[Income] = 0 and l.[MsgIdx] = (select [Idx] from @msg where [Num] = 1) and l.[MsgToUserIdx] = (select [Idx2] from @msg where [Num] = 1) then 1 else 0 end from [Msg].[LastMessageView] l where l.[UserId] = @u1 and l.[UserOppId] = @u2;
--#2: first message for user2 select case when l.[MsgId] = (select [Id] from @msg where [Num] = 1) and l.[ToTransportTypeId] = 0 and l.[Income] = 1 and l.[MsgIdx] = (select [Idx] from @msg where [Num] = 1) and l.[MsgToUserIdx] = (select [Idx2] from @msg where [Num] = 1) then 1 else 0 end from [Msg].[LastMessageView] l where l.[UserId] = @u2 and l.[UserOppId] = @u1;
--send second message flchat->test insert into @msg exec [Test].[SendMessage] @from=@u1, @fromTrans=0, @to=@u2, @toTrans=-1;
--#4: second message for user1 select case when l.[MsgId] = (select [Id] from @msg where [Num] = 2) and l.[ToTransportTypeId] = -1 and l.[Income] = 0 and l.[MsgIdx] = (select [Idx] from @msg where [Num] = 2) and l.[MsgToUserIdx] = (select [Idx2] from @msg where [Num] = 2) then 1 else 0 end from [Msg].[LastMessageView] l where l.[UserId] = @u1 and l.[UserOppId] = @u2;
--#5: first message for user2 (second message was sent to outer message) select case when l.[MsgId] = (select [Id] from @msg where [Num] = 1) and l.[ToTransportTypeId] = 0 and l.[Income] = 1 and l.[MsgIdx] = (select [Idx] from @msg where [Num] = 1) and l.[MsgToUserIdx] = (select [Idx2] from @msg where [Num] = 1) then 1 else 0 end from [Msg].[LastMessageView] l where l.[UserId] = @u2 and l.[UserOppId] = @u1;
--send third message test->flchat insert into @msg exec [Test].[SendMessage] @from=@u1, @fromTrans=-1, @to=@u2, @toTrans=0;
--#6: second message for user1 (third message was sent from outer transport) select case when l.[MsgId] = (select [Id] from @msg where [Num] = 2) and l.[ToTransportTypeId] = -1 and l.[Income] = 0 and l.[MsgIdx] = (select [Idx] from @msg where [Num] = 2) and l.[MsgToUserIdx] = (select [Idx2] from @msg where [Num] = 2) then 1 else 0 end from [Msg].[LastMessageView] l where l.[UserId] = @u1 and l.[UserOppId] = @u2;
--#7: third message for user2 select case when l.[MsgId] = (select [Id] from @msg where [Num] = 3) and l.[ToTransportTypeId] = 0 and l.[Income] = 1 and l.[MsgIdx] = (select [Idx] from @msg where [Num] = 3) and l.[MsgToUserIdx] = (select [Idx2] from @msg where [Num] = 3) then 1 else 0 end from [Msg].[LastMessageView] l where l.[UserId] = @u2 and l.[UserOppId] = @u1;
--#8 check view data has not dublicates select 1 from [Msg].[LastMessageView] group by [UserId], [UserOppId] having count(*) > 1
--clear data delete from [Msg].[Message] where [FromUserId] = @u1; delete from [Usr].[User] where [Id] in (@u1, @u2);
